import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import './my-profile.css';

const MyProfile = (props) => {
  // State to store the uploaded profile picture
  const [profilePicture, setProfilePicture] = useState(null);

  // Function to handle file upload
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        setProfilePicture(reader.result);
      };
    }
  };

  return (
    <div className="my-profile-container">
      <Helmet>
        <title>My-Profile - ServeQuest</title>
        <meta property="og:title" content="My-Profile - ServeQuest" />
      </Helmet>
      <div className="my-profile-calendar">
        {/* Rest of your existing JSX */}
        <input
          type="file"
          accept="image/*"
          onChange={handleFileUpload}
          style={{ display: 'none' }}
          id="profile-picture-input"
        />
        <label htmlFor="profile-picture-input">
          <img
            src={profilePicture ? profilePicture : 'https://play.teleporthq.io/static/svg/default-img.svg'}
            alt="image"
            id="userpfp"
            className="my-profile-image thq-img-round"
          />
        </label>
        {/* Rest of your existing JSX */}
      </div>
    </div>
  );
};

export default MyProfile;
