import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import './my-profile.css';

const MyProfile = (props) => {
  // State to hold the user's bio text
  const [bio, setBio] = useState('');

  // Function to handle changes in the textarea
  const handleBioChange = (event) => {
    setBio(event.target.value);
  };

  // Function to send bio data to the server
  const saveBio = () => {
    // Send a POST request to your server's endpoint
    fetch('/insert_bio', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ bio }),
    })
      .then((response) => {
        // Handle response from the server if needed
        if (response.ok) {
          console.log('Bio saved successfully');
        } else {
          console.error('Failed to save bio');
        }
      })
      .catch((error) => {
        console.error('Error saving bio:', error);
      });
  };

  return (
    <div className="my-profile-container">
      <Helmet>
        <title>My-Profile - ServeQuest</title>
        <meta property="og:title" content="My-Profile - ServeQuest" />
      </Helmet>
      {/* Your other JSX content */}
      <textarea
        placeholder="Type something here!"
        className="my-profile-textarea textarea"
        value={bio}
        onChange={handleBioChange}
      ></textarea>
      {/* Save button */}
      <button onClick={saveBio}>Save Bio</button>
    </div>
  );
};

export default MyProfile;
