import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import './explore.css';

const Explore = (props) => {
  // Sample data for volunteer activities
  const [activities, setActivities] = useState([
    {
      name: 'Activity 1',
      image: 'activity1.jpg',
      category: 'Category 1',
      location: 'Location 1',
    },
    {
      name: 'Activity 2',
      image: 'activity2.jpg',
      category: 'Category 2',
      location: 'Location 2',
    },
    // Add more activities as needed
  ]);

  const handleActivityClick = (activity) => {
    // Call API endpoint with activity details
    console.log('Clicked on activity:', activity);
    // Example API call:
    // fetch('http://example.com/endpoint', {
    //   method: 'POST',
    //   body: JSON.stringify(activity),
    //   headers: {
    //     'Content-Type': 'application/json'
    //   }
    // })
    // .then(response => response.json())
    // .then(data => console.log(data))
    // .catch(error => console.error('Error:', error));
  };

  return (
    <div className="explore-container">
      <Helmet>
        <title>Explore - ServeQuest</title>
        <meta property="og:title" content="Explore - ServeQuest" />
      </Helmet>
      <div className="explore-calendar">
        {/* Navigation bar */}
        <header data-thq="thq-navbar" className="explore-navbar-interactive">
          {/* Logo and menu */}
          {/* Your navigation links */}
        </header>
        <span className="explore-text06">Explore</span>
        {/* Scrollable list of activities */}
        <div className="explore-activity-list-container">
          <div className="explore-activity-list">
            {/* Map through activities and render buttons */}
            {activities.map((activity, index) => (
              <div key={index} className="explore-activity-item">
                <button
                  className="explore-activity-button"
                  onClick={() => handleActivityClick(activity)}
                >
                  <div className="explore-activity-details">
                    <img
                      src={activity.image}
                      alt={activity.name}
                      className="explore-activity-image"
                    />
                    <div className="explore-activity-info">
                      <span className="explore-activity-name">{activity.name}</span>
                      <span className="explore-activity-category">{activity.category}</span>
                      <span className="explore-activity-location">{activity.location}</span>
                    </div>
                  </div>
                </button>
              </div>
            ))}
          </div>
        </div>
        <span className="explore-text08">
          <span>Here are some activities catered to your profile. </span>
          <span>Gain experience points as you do activities!</span>
        </span>
      </div>
    </div>
  );
};

export default Explore;
