import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import './my-profile.css';

const MyProfile = (props) => {
  const [profileImage, setProfileImage] = useState(null);

  // Function to handle file upload
  const handleImageUpload = (event) => {
    const imageFile = event.target.files[0];
    if (imageFile) {
      // You may want to perform additional checks/validation here
      const imageUrl = URL.createObjectURL(imageFile);
      setProfileImage(imageUrl);
    }
  };

  return (
    <div className="my-profile-container">
      <Helmet>
        <title>My-Profile - ServeQuest</title>
        <meta property="og:title" content="My-Profile - ServeQuest" />
      </Helmet>
      <div className="my-profile-calendar">
        {/* Your existing content */}
        <img
          src={profileImage || 'https://play.teleporthq.io/static/svg/default-img.svg'}
          alt="image"
          id="userpfp"
          className="my-profile-image thq-img-round"
        />
        {/* Add a file input field for image upload */}
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="my-profile-upload-input"
        />
        {/* Your existing content */}
      </div>
    </div>
  );
};

export default MyProfile;
